INSERT INTO `gm_plate` VALUES ('10a42ff51f914802a9eec5dcdbc2c374', 'fac18df4ac5f4d2fafc35405f71a63a9', '程序设计语言', '/image/编程.jpg', '<div class=\"para\" label-module=\"para\" style=\"overflow-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 28px; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif; background-color: rgb(255, 255, 255);\"><a target=\"_blank\" href=\"https://baike.baidu.com/item/%E7%A8%8B%E5%BA%8F%E8%AE%BE%E8%AE%A1\" style=\"color: rgb(19, 110, 194); text-decoration-line: underline; outline: 0px;\">程序设计</a>语言是用于书写计算机程序的语言。语言的基础是一组记号和一组规则。根据规则由记号构成的记号串的总体就是语言。在程序设计语言中，这些记号串就是程序。程序设计语言有3个方面的因素，即语法、语义和语用。语法表示程序的结构或形式，亦即表示构成语言的各个记号之间的组合规律，但不涉及这些记号的特定含义，也不涉及使用者。语义表示程序的含义，亦即表示按照各种方法所表示的各个记号的特定含义，但不涉及使用者。</div>', NULL);
